import org.omg.CORBA.*; import org.omg.CosNaming.*; import AutoTrack.*; public class CorbaClient { public static void main(String[] args) { try { ORB orb = ORB.init(args, null); org.omg.CORBA.Object objRef = orb.resolve_initial_references("NameService"); NamingContextExt ncRef = NamingContextExtHelper.narrow(objRef); AutoTrack.MileageProvider href = AutoTrack.MileageProviderHelper.narrow(ncRef.resolve_str("MileageProvider")); System.out.println("Mileage from CORBA provider: " + href.getMileage()); } catch (Exception e) { e.printStackTrace(); } } }
