import java.rmi.registry.LocateRegistry; import java.rmi.registry.Registry; public class RmiClient { public static void main(String[] args) { try { Registry registry = LocateRegistry.getRegistry("localhost"); RemoteService stub = (RemoteService) registry.lookup("AutoTrackService"); VehicleInfo myCar = new VehicleInfo("Toyota Yaris", 150000, 2015); System.out.println("Envoi de l'objet : " + myCar.toString()); String response = stub.analyzeDiagnostic(myCar); System.out.println("Reponse du Serveur RMI : " + response); } catch (Exception e) { e.printStackTrace(); } } }
